css:
.banners-grid.my-3.my-md-4.my-lg-5 div.col-12.col-lg-6.mb-3.mb-lg-0 {
    max-width: 50%;
    flex: 0 0 50%;
    padding-right: 10px;	 
    padding-left: 10px;	
  }

@media(min-width: 600px) {
  #logo {
    max-height: 100px !important;
  } 
} 

.product__prices .prices{
  display: flex;
  flex-direction: column
}
.product__prices .prices strong + span {
  order: -1;
  display: flex;
  flex-direction: column;
}
.product__prices .prices strong + span .prices__discount {
  order: -1;
  font-size: var(--h1)
}
.product__prices .prices .prices__value {
  font-size: var(--font-size);
}

.product-card__prices {
  display: flex;
  flex-direction: column
}
.product-card__prices strong + span {
  order: -1;
  display: flex;
  flex-direction: column;
}
.product-card__prices strong + span .prices__discount {
  order: -1;
  font-size: var(--font-size-lg)
}
.product-card__prices .prices__value {
  font-size: var(--font-size-sm);
}

.product-card__prices .prices__discount span, .product__prices .prices strong + span .prices__discount span {
  color: #ff3e89
}
