css:
.banners-grid.my-3.my-md-4.my-lg-5 div.col-12.col-lg-6.mb-3.mb-lg-0 {
    max-width: 50%;
    flex: 0 0 50%;
    padding-right: 10px;	 
    padding-left: 10px;	
  }

@media(min-width: 600px) {
  #logo {
    max-height: 100px !important;
  } 
}
.fa-chevron-right{
  color: #ff3e89;
}
.fa-chevron-left{
  color: #ff3e89;
}
.header__toggler-icon{
  font-size: 1.7rem;
}
.list-group-item.active{
  background-color: #ff3e89;
}
.badge-info{
  background-color: #ff3e89;
}
.shipping-calculator__free-from-value .progress{
  background-color: #ffd9e8;
}
.shipping-calculator__free-from-value .progress-bar-striped{
  background-image: linear-gradient(45deg,#ff86b5 25%,#ff3e89 0,#ff3e89 50%,#ff86b5 0,#ff86b5 75%,#ff3e89 0,#ff3e89);
}
.discount-applier .alert {
  background-color: var(--primary);
}
.shipping-calculator__option .shipping-line>small {
    color: var(--text);
}
.menu__social a {
  font-size: xx-large;
}
.footer__social {
    font-size: xx-large;
}
